import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Slider from "react-slick"
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";


function Main({user}) {
	// -- Slide 1 -- //
	const slides1 = {
		dots: false,
		infinite: true,
		autoplay:true,
		autoplayTimeout:100,
		slidesToShow: 1,
		slidesToScroll: 1,
		initialSlide: 0,
		responsive: [
		{
			breakpoint: 1024,
			settings: {
			  slidesToShow: 1,
			  slidesToScroll:1,
			  infinite: true,
			  dots: false
			}
		  },
		  {
			breakpoint: 600,
			settings: {
			  slidesToShow: 1,
			  slidesToScroll: 1,
			  initialSlide: 1
			}
		  },
		  {
			breakpoint: 480,
			settings: {
			  slidesToShow: 1,
			  slidesToScroll: 1
			}
		  }
		]
	  };
	// Api

	const [carousel1, setCarrousel1] = useState([]);

    useEffect(() => {

    axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/carrousel-main').then(res => {

		const carousel1 = res.data.collection;
		setCarrousel1(carousel1);

    });

	}, [])
	// -- Cierre Slide 1 -- //

	// -- Slide 2 -- //
	const slides2 = {
		dots: false,
		infinite: true,
		speed: 500,
		slidesToShow: 4,
		slidesToScroll: 4,
		initialSlide: 0,
		responsive: [
		{
			breakpoint: 1300,
			settings: {
			  slidesToShow: 3,
			  slidesToScroll: 3,
			  infinite: true,
			  dots: false
			}
		  },
		  {
			breakpoint: 1000,
			settings: {
			  slidesToShow: 2,
			  slidesToScroll: 2,
			  infinite: true,
			  dots: false
			}
		  },
		  {
			breakpoint: 700,
			settings: {
			  slidesToShow: 1,
			  slidesToScroll: 1,
			  initialSlide: 1
			}
		  },
		  {
			breakpoint: 480,
			settings: {
			  slidesToShow: 1,
			  slidesToScroll: 1
			}
		  }
		]
	  };
	// Api
	const [title2, setTitle2] = useState([]);
	const [carousel2, setCarrousel2] = useState([]);

    useEffect(() => {

    axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/megaofertas').then(res => {
		const title2 = res.data.title;
		const carousel2 = res.data.collection;
		setCarrousel2(carousel2);
		setTitle2(title2);

    });

	}, [])
	// -- Cierre Slide 2 -- //


	// -- Slide 3 -- //
	const slides3 = {
		dots: false,
		infinite: true,
		speed: 500,
		slidesToShow: 4,
		slidesToScroll: 4,
		initialSlide: 0,
		responsive: [
		{
			breakpoint: 1300,
			settings: {
			  slidesToShow: 3,
			  slidesToScroll: 3,
			  infinite: true,
			  dots: false
			}
		  },
		  {
			breakpoint: 900,
			settings: {
			  slidesToShow: 2,
			  slidesToScroll: 2,
			  infinite: true,
			  dots: false
			}
		  },
		  {
			breakpoint: 700,
			settings: {
			  slidesToShow: 1,
			  slidesToScroll: 1,
			  initialSlide: 1
			}
		  },
		  {
			breakpoint: 480,
			settings: {
			  slidesToShow: 1,
			  slidesToScroll: 1
			}
		  }
		]
	  };
	// Api
	const [title3, setTitle3] = useState([]);
	const [carousel3, setCarrousel3] = useState([]);

    useEffect(() => {

    axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/favoritos').then(res => {
		const title3 = res.data.title;
		const carousel3 = res.data.collection;
		setTitle3(title3);
		setCarrousel3(carousel3);
    });

	}, [])
	// -- Cierre Slide 3 -- //


	// -- Slide 4 -- //
	const slides4 = {
		dots: false,
		infinite: true,
		speed: 500,
		slidesToShow: 4,
		slidesToScroll: 4,
		initialSlide: 0,
		responsive: [
		{
			breakpoint: 1300,
			settings: {
			  slidesToShow: 3,
			  slidesToScroll: 3,
			  infinite: true,
			  dots: false
			}
		  },
		  {
			breakpoint: 900,
			settings: {
			  slidesToShow: 2,
			  slidesToScroll: 2,
			  infinite: true,
			  dots: false
			}
		  },
		  {
			breakpoint: 700,
			settings: {
			  slidesToShow: 1,
			  slidesToScroll: 1,
			  initialSlide: 1
			}
		  },
		  {
			breakpoint: 480,
			settings: {
			  slidesToShow: 1,
			  slidesToScroll: 1
			}
		  }
		]
	  };
	// Api
	const [title4, setTitle4] = useState([]);
	const [carousel4, setCarrousel4] = useState([]);

    useEffect(() => {

    axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/temporada').then(res => {
		const title4 = res.data.title;
		const carousel4 = res.data.collection;
		setTitle4(title4);
		setCarrousel4(carousel4);
    });

	}, [])
	// -- Cierre Slide 4 -- //


	// -- Slide 5 -- //
	const slides5 = {
		dots: false,
		infinite: true,
		speed: 500,
		slidesToShow: 4,
		slidesToScroll: 1,
		initialSlide: 0,
		responsive: [
		{
			breakpoint: 1300,
			settings: {
			  slidesToShow: 3,
			  slidesToScroll: 3,
			  infinite: true,
			  dots: false
			}
		  },
		  {
			breakpoint: 900,
			settings: {
			  slidesToShow: 2,
			  slidesToScroll: 2,
			  infinite: true,
			  dots: false
			}
		  },
		  {
			breakpoint: 700,
			settings: {
			  slidesToShow: 1,
			  slidesToScroll: 1,
			  initialSlide: 1
			}
		  },
		  {
			breakpoint: 480,
			settings: {
			  slidesToShow: 1,
			  slidesToScroll: 1
			}
		  }
		]
	  };
	// Api
	const [title5, setTitle5] = useState([]);
	const [carousel5, setCarrousel5] = useState([]);

    useEffect(() => {

    axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/bloqueos').then(res => {
		const carousel5 = res.data.collection;
		setCarrousel5(carousel5);
		const title5 = res.data.title;
        setTitle5(title5);
    });

	}, [])
	// -- Cierre Slide 5 -- //

	// -- Mega traveler -- //
	const [traveler, setTraveler] = useState([]);

    useEffect(() => {

    axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/revista-magazine').then(res => {
		const traveler = res.data;

		setTraveler(traveler);
    });

	}, [])
	// -- Fin Mega traveler -- //

	// -- Mega en Vivo -- //

	const [megatitle, setTitle] = useState([]);
	const [megatv, setMegatv] = useState([]);

    useEffect(() => {

    axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/megatv').then(res => {
		const megatv = res.data.collection;
		setMegatv(megatv);
		const megatitle = res.data.title;
		setTitle(megatitle);
    });

	}, [])
	// -- Fin Mega en Vivo -- //
	let numeromayor = 0;
	const [ open, setOpen ] = useState(false)
	const [ open2, setOpen2 ] = useState(false)

    return (
        <>
	<div className="main">

		<section className="main__header frutos">
			<nav className={ open ? "sidebar show" : "sidebar"}>
				<div className="aside__icon__close">
					<a aria-expanded={open === true ? "true" : "false"}
					className={open === true ? "aside__icon-a1" : "aside__icon-a1"}
					onClick={ () => setOpen(!open) }>
					<i class="fas fa-window-close" aria-hidden="true"></i>
					</a>
				</div>

				<div className="aside__icon">
					<a aria-expanded={open === true ? "true" : "false"}
					className={open === true ? "aside__icon-a1" : "aside__icon-a1"}
					onClick={ () => setOpen(!open) }/>
				</div>

				<ul>
					<li>
						<a href="/promociones">
						ofertas y <br /> promociones
						</a>
					</li>
					<li>
						<a  aria-expanded={open2 === true ? "true" : "false"}
						className={open2 === true ? "feat-btn" : "feat-btn"}
						onClick={ () => setOpen2(!open2) }>
								destinos <br /> de viaje
								<span className="fas fa-chevron-circle-down first"></span>
						</a>
						<ul className={ open2 ? "feat-show show" : "feat-show"}>
							<li>
								<a href="/europa">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/viajes-a-europa.jpg" alt="" />
								<span className="span__submenu">viajes a europa</span>
								</a>
							</li>
							<li>
								<a href="/canada">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/canada.jpg" alt="" />
								<span className="span__submenu">canadá</span>
								</a>
							</li>
							<li>
								<a href="/estados-unidos">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/estados-unidos.jpg" alt="" />
								<span className="span__submenu">estados unidos</span>
								</a>
							</li>
							<li>
								<a href="/mexico">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/mexico.jpg" alt="" />
								<span className="span__submenu">méxico</span>
								</a>
							</li>
							<li>
								<a href="/sudamerica">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/sudamerica.jpg" alt="" />
								<span className="span__submenu">sudamérica</span>
								</a>
							</li>
							<li>
								<a href="/centroamerica">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/centroamerica.jpg" alt="" />
								<span className="span__submenu font">centroamérica</span>
								</a>
							</li>
							<li>
								<a href="/pacifico">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/pacifico.jpg" alt="" />
								<span className="span__submenu">pacífico</span>
								</a>
							</li>
							<li>
								<a href="/medio-oriente">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/medio-oriente.jpg" alt="" />
								<span className="span__submenu">medio oriente</span>
								</a>
							</li>
							<li>
								<a href="/asia">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/asia.jpg" alt="" />
								<span className="span__submenu">asia</span>
								</a>
							</li>
							<li>
								<a href="/africa">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/africa.jpg" alt="" />
								<span className="span__submenu">africa</span>
								</a>
							</li>
							<li>
								<a href="/eventos">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/eventos.jpg" alt="" />
								<span className="span__submenu">eventos</span>
								</a>
							</li>
							<li>
								<a href="/cruceros">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/cruceros.jpg" alt="" />
								<span className="span__submenu">cruceros</span>
								</a>
							</li>

							<li>
								<a href="/juvi">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/juvi.jpg" alt="" />
								<span className="span__submenu">#juvi</span>
								</a>
							</li>

							<li>
								<a href="/exoticos">
								<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/menu/grupos-y-fits.jpg" alt="" />
								<span className="span__submenu">Exoticos</span>
								</a>
							</li>
						</ul>
					</li>
					<li>
						<a aria-expanded={open === true ? "true" : "false"}
						className={open === true ? "feat-showsomos showsomos" : "feat-showsomos"}
						onClick={ () => setOpen(!open) } href="/#somos">quienes somos</a>
					</li>
					<li>
						<a aria-expanded={open === true ? "true" : "false"}
						className={open === true ? "feat-show1 show1" : "feat-show1"}
						onClick={ () => setOpen(!open) } href="/#somosagencia">quienes somos</a>
					</li>
					<li>
						<a aria-expanded={open === true ? "true" : "false"}
						className={open === true ? "feat-show show" : "feat-show"}
						onClick={ () => setOpen(!open) } href="#contacto">contacto</a>
					</li>
				</ul>
			</nav>

			<div className="aside__icon">
				<a aria-expanded={open === true ? "true" : "false"}
				className={open === true ? "aside__icon-a" : "aside__icon-a"}
				onClick={ () => setOpen(!open) }>
				<i class="fa fa-bars" aria-hidden="true"></i>
				</a>
			</div>

			<h1 className="main__h1">
                la mejor agencia <span className="main__span back__frutos">de viajes</span>
                <div className="maple">
					<img class="maple-img" src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/maple.png" alt=""/>
				</div>
            </h1>

            <div className="woman">
                <img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/chica-frutos-rojos.png" alt="" className="woman__img" />
            </div>
        </section>

		<section className="main__carousel">
            <div className="main__carousel-owl owl-carousel owl-theme">
				<Slider {...slides1}>
					{carousel1.map(c1 => (			
						<div className="item main__carousel-owl-item" key={c1.mt}>
							<a href={"detail/"+c1.mt+"/"+c1.name} className="main__carousel-owl-item-link">
								<img src={c1.img1} alt="" className="slide_desktop main__carousel-owl-item-image" /> 
								<img className="slide_movil img-fluid" src={c1.img2} alt=""/>
							</a> 
							<div className="main__carousel-owl-info">
								<div className="main__carousel-owl-price"><span className="main__carousel-owl-since">DESDE</span>
									{c1.price_from}
								</div><br/> 
									<span className="main__carousel-owl-current">{c1.currency} {c1.room_type } + {c1.taxes}*</span>
							</div>
                    	</div>
					))}
				</Slider>      
            </div>
        </section>

        <section className="main__carousel-hot back__frutos">
            <h3 className="title__sections">
				<span className="title__sections-icon">
                <img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/play-white.png" alt="" className="title__sections-img"/>
                </span> 
                <p className="content__title">
                    lo mas <span className="title__sections-hot">hot</span>
                </p>
				<div className="aguacate">
                    <img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/frutos1.png" alt="" className="responsive__aguacate"/>
                </div>
			</h3>
			<div className="main__sections owl-carousel owl-theme">
				<Slider {...slides2}>
					{carousel2.map(c2 => (
						<div className="item main__sections-item" key={c2.mt}>
							<a href={"detail/"+c2.mt+"/"+c2.name} className="main__sections-item-link">
								<img src={c2.img} alt="" className="main__sections-item-image" /></a>
							<div className="main__sections-mt">MT-{c2.mt}
							</div> 
							<div className="main__sections-info">
								<small className="main__sections-days">{c2.days} DÍAS / {c2.nights} NOCHES</small> 
									<div className="main__sections-price">
										<span className="main__sections-since">DESDE</span>{c2.price_from}
									</div><br/> 
								<span className="main__sections-current">{c2.currency} {c2.room_type}+{c2.taxes}*</span>
							</div>
						</div>
					))}
				</Slider>
            </div>
			<div class="aguacate__sub">
				<img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/lentes.png" alt="" class="responsive__aguacate-sub"/>
			</div>
		</section>

		<section className="travels__carousel__section back__frutos__favoritos">
            <h3 className="travels__title__sections color__black">
				<span className="title__sections-icon">
                <img src="https://exodus.b-cdn.net/exodus/frutas/frutos-rojos/img/play.png" alt="" className="title__sections-img"/>
                </span> 
                <p className="content__title">
                     mis viajes <span className="title__sections-hot">favoritos</span>
                </p>
            </h3>
			<div className="main__sections owl-carousel owl-theme">
				<Slider {...slides3}>
					{carousel3.map(c3 => (
						<div className="item main__sections-item" key={c3.mt}>
							<a href={"detail/"+c3.mt+"/"+c3.name} className="main__sections-item-link">
								<img src={c3.img} alt="" className="main__sections-item-image" /></a>
							<div className="main__sections-mt">MT-{c3.mt}
							</div> 
							<div className="main__sections-info">
								<small className="main__sections-days">{c3.days} DÍAS / {c3.nights} NOCHES</small> 
								<div className="main__sections-price">
									<span className="main__sections-since">DESDE</span>{c3.price_from}
								</div><br/> 
								<span className="main__sections-current">{c3.currency} {c3.room_type}+{c3.taxes}*</span>
							</div>
						</div>
					))}
				</Slider>
			</div>
        </section>

        <section className="verano__carousel__section">
