import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Slider from "react-slick"
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

function Main({user}) {

// -- Slide 1 -- //
const slides1 = {
	dots: false,
	autoplay: true,
	infinite: true,
	speed: 500,
	slidesToShow: 1,
	slidesToScroll: 1,
	initialSlide: 0,
	responsive: [
	{
		breakpoint: 1024,
		settings: {
		  slidesToShow: 3,
		  slidesToScroll: 3,
		  infinite: true,
		  dots: false
		}
	  },
	  {
		breakpoint: 600,
		settings: {
		  slidesToShow: 1,
		  slidesToScroll: 1,
		  initialSlide: 1
		}
	  },
	  {
		breakpoint: 480,
		settings: {
		  slidesToShow: 1,
		  slidesToScroll: 1
		}
	  }
	]
  };
// Api

const [carousel1, setCarrousel1] = useState([]);

useEffect(() => {

axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/carrousel-main').then(res => {
	
	const carousel1 = res.data.collection;
	setCarrousel1(carousel1);
	
});

}, [])
// -- Cierre Slide 1 -- //

// -- Slide 2 -- //
const slides2 = {
	dots: false,
	autoplay: true,
	infinite: true,
	speed: 500,
	slidesToShow: 3,
	slidesToScroll: 3,
	initialSlide: 0,
	responsive: [
	{
		breakpoint: 1024,
		settings: {
		  slidesToShow: 3,
		  slidesToScroll: 3,
		  infinite: true,
		  dots: false
		}
	  },
	  {
		breakpoint: 600,
		settings: {
		  slidesToShow: 1,
		  slidesToScroll: 1,
		  initialSlide: 1
		}
	  },
	  {
		breakpoint: 480,
		settings: {
		  slidesToShow: 1,
		  slidesToScroll: 1
		}
	  }
	]
  };
// Api
const [title2, setTitle2] = useState([]);
const [carousel2, setCarrousel2] = useState([]);

useEffect(() => {

axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/megaofertas').then(res => {
	const title2 = res.data.title;
	const carousel2 = res.data.collection;
	setCarrousel2(carousel2);
	setTitle2(title2);

	
	
});

}, [])
// -- Cierre Slide 2 -- //


// -- Slide 3 -- //
const slides3 = {
	dots: false,
	autoplay: true,
	infinite: true,
	speed: 500,
	slidesToShow: 3,
	slidesToScroll: 1,
	initialSlide: 0,
	responsive: [
	{
		breakpoint: 1024,
		settings: {
		  slidesToShow: 3,
		  slidesToScroll: 3,
		  infinite: true,
		  dots: false
		}
	  },
	  {
		breakpoint: 600,
		settings: {
		  slidesToShow: 1,
		  slidesToScroll: 1,
		  initialSlide: 1
		}
	  },
	  {
		breakpoint: 480,
		settings: {
		  slidesToShow: 1,
		  slidesToScroll: 1
		}
	  }
	]
  };
// Api
const [title3, setTitle3] = useState([]);
const [carousel3, setCarrousel3] = useState([]);

useEffect(() => {

axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/favoritos').then(res => {
	const title3 = res.data.title;
	const carousel3 = res.data.collection;
	setTitle3(title3);
	setCarrousel3(carousel3);
});

}, [])
// -- Cierre Slide 3 -- //


// -- Slide 4 -- //
const slides4 = {
	dots: false,
	autoplay: true,
	infinite: true,
	speed: 500,
	slidesToShow: 4,
	slidesToScroll: 4,
	initialSlide: 0,
	responsive: [
	{
		breakpoint: 1024,
		settings: {
		  slidesToShow: 3,
		  slidesToScroll: 3,
		  infinite: true,
		  dots: false
		}
	  },
	  {
		breakpoint: 600,
		settings: {
		  slidesToShow: 1,
		  slidesToScroll: 1,
		  initialSlide: 1
		}
	  },
	  {
		breakpoint: 480,
		settings: {
		  slidesToShow: 1,
		  slidesToScroll: 1
		}
	  }
	]
  };
// Api
const [title4, setTitle4] = useState([]);
const [carousel4, setCarrousel4] = useState([]);

useEffect(() => {

axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/temporada').then(res => {
	const title4 = res.data.title;
	const carousel4 = res.data.collection;
	setTitle4(title4);
	setCarrousel4(carousel4);
});

}, [])
// -- Cierre Slide 4 -- //


// -- Slide 5 -- //
const slides5 = {
	dots: false,
	autoplay: true,
	infinite: true,
	speed: 500,
	slidesToShow: 3,
	slidesToScroll: 1,
	initialSlide: 0,
	responsive: [
	{
		breakpoint: 1024,
		settings: {
		  slidesToShow: 3,
		  slidesToScroll: 3,
		  infinite: true,
		  dots: false
		}
	  },
	  {
		breakpoint: 600,
		settings: {
		  slidesToShow: 1,
		  slidesToScroll: 1,
		  initialSlide: 1
		}
	  },
	  {
		breakpoint: 480,
		settings: {
		  slidesToShow: 1,
		  slidesToScroll: 1
		}
	  }
	]
  };
// Api
const [title5, setTitle5] = useState([]);
const [carousel5, setCarrousel5] = useState([]);

useEffect(() => {

axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/bloqueos').then(res => {
	const carousel5 = res.data.collection;
	setCarrousel5(carousel5);
	const title5 = res.data.title;
	setTitle5(title5);
});

}, [])
// -- Cierre Slide 5 -- //

// -- Mega traveler -- //
const [traveler, setTraveler] = useState([]);

useEffect(() => {

axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/revista-magazine').then(res => {
	const traveler = res.data;
	
	setTraveler(traveler);
});

}, [])
// -- Fin Mega traveler -- //

// -- Mega en Vivo -- //

const [megatitle, setTitle] = useState([]);
const [megatv, setMegatv] = useState([]);

useEffect(() => {

axios.get('https://www.megatravel.com.mx/developers/cafe-carousel/megatv').then(res => {
	const megatv = res.data.collection;
	setMegatv(megatv);
	const megatitle = res.data.title;
	setTitle(megatitle);
});

}, [])
// -- Fin Mega en Vivo -- //


    return (
        <>
         <main>
        <section className="agencia">
            <div className="container-template">
                <div className="row">
                    <div className="col-md-6">
                        <img src="https://exodus.b-cdn.net/exodus/prime/01/img/chica-sombrero.png" className="img-fluid" alt="agencia de viajes"/>
                    </div>
                    <div className="col-md-6">
                        <h1 className="title-h1">La mejor <br/> Agencia de<br/> Viajes</h1>
                        <h2 className="title-h2">Creamos experiencias inolvidables <br/> en Mxico y el mundo</h2>
                        <a href="#contacto" className="btn-section-one btn-back-azul-cafe">Quiero recibir asesora</a>
                    </div>
                </div>
            </div>

            <div className="divider background-azul"></div>
        </section>

        <section className="confiarnos background-azul">
            <div className="container-template text-center">
                <h3 className="title-section-white">
                    Razones para confiarnos tu viaje
                </h3>
                <h4 className="subtitle-section-white">Contar con la asesora y el respaldo de un Agente de viajes, es garanta de:</h4>
                
                <div className="row items-confiarnos">

                    <div className="col-md-12 d-flex flex-wrap justify-content-center">
                       
                        <div className="m-2 text-dark">
                        <a href="" className="media rounded align-items-center pl-3 pr-4 pl-md-4 pr-md-5 py-2 link-confiranos">
                            <span className="span-confiarnos">
                                <img src="https://exodus.b-cdn.net/exodus/prime/01/img/planeacion.png" alt=""/>
                            </span> planeacin sin contratiempos
                        </a>
                        </div>
                        <div className="m-2 text-dark">
                        <a href="" className="media rounded align-items-center pl-3 pr-4 pl-md-4 pr-md-5 py-2 link-confiranos">
                            <span className="span-confiarnos">
                                <img src="https://exodus.b-cdn.net/exodus/prime/01/img/asesoria.png" alt=""/>
                            </span> asesora personalizada
                        </a>
                        </div>
                        <div className="m-2 text-dark">
                        <a href="" className="media rounded align-items-center pl-3 pr-4 pl-md-4 pr-md-5 py-2 link-confiranos">
                            <span className="span-confiarnos">
                                <img src="https://exodus.b-cdn.net/exodus/prime/01/img/precios-competitivos.png" alt=""/>
                            </span> precios competitivos
                        </a>
                        </div> 

                        
                        </div>

                        <div className="col d-flex flex-wrap justify-content-center">
                            <div className="m-2 text-dark">
                                <a href="" className="media rounded align-items-center pl-3 pr-4 pl-md-4 pr-md-5 py-2 link-confiranos">
                                    <span className="span-confiarnos">
                                        <img src="https://exodus.b-cdn.net/exodus/prime/01/img/optimizacion.png" alt=""/>
                                    </span> optimizacin de tu tiempo antes y durante el viaje
                                </a>
                                </div> 
                        </div>

                </div>
            </div>
        </section>


        <section className="back-banner">
        
            <div className="space-banner"></div>
                  
            <div className="divider divider-bottom bg-white"></div>

        </section>
        
        
        <section className="mas-vendidos">
            <div className="container-template-slide">
                <h3 className="title-section-black">Los ms vendidos</h3>
                <h4 className="subtitle-section-black">Destinos de temporada irresistible</h4>
                <div className="vendidos owl-carousel owl-theme">
                <Slider {...slides2}>
        				{carousel2.map(c2 => (
                  
                  <div class="item mega__ofertas--item" key={c2.mt}> 
                    <a href={"detail/"+c2.mt+"/"+c2.name}>
                      <img src={c2.img} alt=""/>
                      
                    </a>
                    <div class="item-slide-info">
                            <small class="item-slide-days">{c2.days} DAS / {c2.nights} NOCHES</small> 
                            <div class="item-slide-price">
                                <span class="item-slide-since">DESDE</span>{c2.price_from}
                           </div><br/> 
                            <span class="item-slide-current">{c2.currency} {c2.room_type}+{c2.taxes}*</span>
                    </div>
                        
                  </div>

                ))}
                </Slider> 
                
			  
                 
                </div>
            </div>
        </section>


        <section className="carrusel">
            <div className="slide owl-carousel owl-theme">

            <Slider {...slides1}>
				{carousel1.map(c1 => (
					<div className="item item-info" key={c1.mt}>
					<a href={'/detail/'+c1.mt+'/'+c1.name}>
						<img className="slide_desktop img-fluid" src={c1.img1} alt=""/>
						<img className="slide_movil" src={c1.img2} alt=""/>
					</a>
					<div className="info-slide">
					
							<p className="slide-desde">desde {c1.price_from} <span className="slide-span">{c1.currency}</span> </p>
							<p className="slide-days"> {c1.days} dias / {c1.nights} noches </p>
							
					</div>
					</div>
				))}
				</Slider>
                
            </div>
        </section>

        <section className="los-favoritos">
            <div className="container-template-slide">
                <h3 className="title-section-black">Los favoritos</h3>
                <h4 className="subtitle-section-black">Nuestra mejor seleccin de destinos nacionales e internacionales</h4>
                <div className="favoritos owl-carousel owl-theme">
                <Slider {...slides3}>
        			{carousel3.map(c3 => (	
                
                <div className="item item-slide-content" key={c3.mt}>
                    <a href={'/detail/'+c3.mt+'/'+c3.name}>
                        <img src={c3.img} alt=""/>
                    </a>
                    <div className="item-slide-info">
                        <small className="item-slide-days">{c3.days} DAS / {c3.nights} NOCHES</small> 
                        <div className="item-slide-price">
                            <span className="item-slide-since">DESDE</span>{c3.price_from}
                      </div><br/> 
                        <span className="item-slide-current">{c3.currency} {c3.room_type}+{c3.taxes}*</span>
                    </div>
                </div>
                


					
					
					))}
					</Slider> 

                
                </div>
            </div>
        </section>

        <section className="salidas-programadas background-picture-azul">
            <div className="container-template-slide">
                <h3 className="title-section-white">Salidas Programadas</h3>
                <h4 className="subtitle-section-white">Satisfaccion total, todo lo que necesitas viene incluido</h4>
                <div className="row container-iconos-programadas">
                    <div className="col-md-12 d-flex flex-wrap justify-content-center">
                       
                        <div className="m-2 text-dark">
                        <a href="" className="media rounded align-items-center pl-3 pr-3 pl-md-3 pr-md-3 py-2 ">
                            <span className="span-confiarnos">
                                <img src="https://exodus.b-cdn.net/exodus/prime/01/img/vuelos.png" alt=""/>
                            </span> 
                        </a>
                        </div>
                        <div className="m-2 text-dark">
                        <a href="" className="media rounded align-items-center pl-3 pr-3 pl-md-3 pr-md-3 py-2 ">
                            <span className="span-confiarnos">
                                <img src="https://exodus.b-cdn.net/exodus/prime/01/img/hospedaje.png" alt=""/>
                            </span> 
                        </a>
                        </div>
                        <div className="m-2 text-dark">
                        <a href="" className="media rounded align-items-center pl-3 pr-3 pl-md-3 pr-md-3 py-2 ">
                            <span className="span-confiarnos">
                                <img src="https://exodus.b-cdn.net/exodus/prime/01/img/alimentos.png" alt=""/>
                            </span> 
                        </a>
                        </div>
                        
                        <div className="m-2 text-dark">
                            <a href="" className="media rounded align-items-center pl-3 pr-3 pl-md-3 pr-md-3 py-2 ">
                                <span className="span-confiarnos">
                                    <img src="https://exodus.b-cdn.net/exodus/prime/01/img/transportacion.png" alt=""/>
                                </span> 
                            </a>
                            </div>

                        
                        </div>
                </div>
                <div className="programadas owl-carousel owl-theme">

                <Slider {...slides5}>
        			{carousel5.map(c5 => (		
					
              <div className="item item-slide-content" key={c5.mt}>
              <a href={'/detail/'+c5.mt+'/'+c5.name}>
                  <img src={c5.img}  alt=""/>
              </a>
              <div className="item-slide-info">
                  <small className="item-slide-days">{c5.days} DAS / {c5.nights} NOCHES</small> 
                  <div className="item-slide-price">
                      <span className="item-slide-since">DESDE</span>{c5.price_from}
                </div><br/> 
                  <span className="item-slide-current">{c5.currency} {c5.room_type}+{c5.taxes}*</span>
              </div>
              </div>
              ))}
              </Slider> 

                </div>
            </div>
        </section>

        <section className="mega-ofertas mega-ofertas-back background-verano-azul">
            <div className="container-template-slide">
              <h3 className="title-section-white">{title4}</h3>
                <h4 className="subtitle-section-white">No te quedes con las ganas, viaja seguro al mejor precio</h4>
                <div className="temporada owl-carousel owl-theme">
                
                <Slider {...slides4}>
                  {carousel4.map(c4 => (
                    <div className="item item-slide-content item-border-white" key={c4.mt}>
                      <a href={"detail/"+c4.mt+"/"+c4.name}>
                        <img src={c4.img} alt=""/>
                      </a>
                    </div>
                  ))}
                  </Slider>                    
                    
                </div>
            </div>
            <div className="divider divider-bottom bg-white"></div>
        </section>

        <section className="tu-viaje-inicia">
            <div className="container-template">
                <div className="row">
                    <div className="col-md-4">
                        
                            

                                <h3 className="title-megatv">Tu viaje inicia aqu</h3>

                                <a href="" data-toggle="modal" data-target="#exampleModal" className="btn-megatv btn-back-azul-cafe">
                                    <span>
                                        <img src="https://exodus.b-cdn.net/exodus/prime/01/img/mega-tv.png" alt=""/>
                                    </span>MEGA TV
                                </a>
                                <a href="" data-toggle="modal" data-target="#exampleModal2" className="btn-megatv btn-back-azul-morado">
                                    <span>
                                        <img src="https://exodus.b-cdn.net/exodus/prime/01/img/mega-traveler.png" alt=""/>
                                    </span>MEGA traveler
                                </a>
                           
                            
                                
                                
                            
                       
                    </div>
                    <div className="col-md-4">
                        <img src="https://exodus.b-cdn.net/exodus/prime/01/img/smartphone.png" className="img-fluid" alt=""/>
                    </div>
                    <div className="col-md-4">
                        <p className="text-megatv"><strong> Consulta nuestras revistas  y videos </strong> e inspirate con las mejores opciones para tus prximas vacaciones.</p>
                        
                        <div className="alert back-respuesta rounded-lg d-inline-block mb-4">
                            <div className="d-flex align-items-center">
                              <div className="badge badge-pill"><img src="https://exodus.b-cdn.net/exodus/prime/01/img/cliente-opinion.png" alt=""/></div>
                              <div className="mx-3">"Servicio con rpida respuesta"</div>
                            </div>
                          </div>
                    </div>
                </div>
            </div>
        </section>


        <div className="modal fade" id="exampleModal" aria-labelledby="exampleModalLabel" aria-hidden="true">
		<div className="modal-dialog modal-xl">
			<div className="modal-content">
			<div className="modal-header">
				<h5 className="modal-title" id="exampleModalLabel">{megatitle}</h5>
				<button type="button" className="close" data-dismiss="modal" aria-label="Close">
				<span aria-hidden="true">&times;</span>
				</button>
			</div>
			<div className="modal-body">
			

			<div className="container">

    <div className="row">
	{megatv.map(tv => (
		<div className="col-md-4" key={tv.order}>
        <div className="card mb-4 shadow-sm">
		    <img src={tv.img} className="img-fluid" />
            <div className="card-body">
	    <p className="card-text">{tv.title}</p>
            <div className="d-flex justify-content-between align-items-center">
                <div className="btn-group">
                <a data-fancybox href={tv.link} className="btn btn-sm btn-outline-danger">Ver video</a>
                </div>
                
            </div>
            </div>
        </div>
        </div>
		))}
        

      </div>
    </div>

			</div>
			<div className="modal-footer">
				
			</div>
			</div>
		</div>
		</div>

    <div className="modal fade" id="exampleModal2" aria-labelledby="exampleModalLabel" aria-hidden="true">
		<div className="modal-dialog modal-xl">
			<div className="modal-content">
			<div className="modal-header">
				<h5 className="modal-title" id="exampleModalLabel">{traveler.title}</h5>
				<button type="button" className="close" data-dismiss="modal" aria-label="Close">
				<span aria-hidden="true">&times;</span>
				</button>
			</div>
			<div className="modal-body">
			

			<div className="container">

    <div className="row">
    

    <div className="col-md-12">
      <div className="row">
        <div class="col-md-6">
          <h3 class="title-megatv">{traveler.title} - {traveler.date}</h3>
          <a href={traveler.url} target="_blank" class="btn-megatv btn-back-azul-cafe">
            <span><img src="https://exodus.b-cdn.net/exodus/prime/01/img/mega-tv.png" alt=""/>
            </span>VER</a>
          <a href={traveler.descarga} target="_blank" class="btn-megatv btn-back-azul-morado">
              <span><img src="https://exodus.b-cdn.net/exodus/prime/01/img/mega-traveler.png" alt=""/>
              </span>DESCARGAR</a>
        </div>
        <div class="col-md-6">
          <img src={traveler.img} class="img-fluid" alt=""/>
        </div>
      </div>
    </div>
      
        

      </div>
    </div>

			</div>
			<div className="modal-footer">
				
			</div>
			</div>
		</div>
		</div>

       

          <section className="quienes-somos" id="somos">
            <div className="container-template">
                <h3 className="title-section-black">Quienes somos</h3>
                <h4 className="subtitle-section-black"> </h4>
                <div className="container-somos">
                <div className="somos owl-carousel owl-theme">
                    <div className="item item-slide-content">
                        <a href="">
                            <div className="row">
                                <div className="col-md-4">
                                    <img src="https://exodus.b-cdn.net/exodus/prime/01/img/img-quienes-somos.png" alt="" className="mainimg1"/>
                                </div>
                                <div className="col-md-8 p-l-somos">
  <h3 className="somos-title">{user.name_agency}</h3>
                                    <p className="somos-text" dangerouslySetInnerHTML={{__html: user.description}}></p>
                                    <p> <img src={user.logo} alt="" className="mainimg1"/></p>
                                </div>
                            </div>
                            
                        </a>
                        
                    </div>
                    
                    

                    

                </div>  
            </div> 
            </div>    
        </section>

        
</main>
        </>
    )
}

export default Main
